import React from 'react';
import AllItems from '../all_items/AllItems';

const PreviewAllItem = props => {
  console.log(props);
  const { category, items } = props;
  return (
    <div className="preview-all-items">
      <h1 className="item-name">{category.toUpperCase()}</h1>
      <div className="preview-container">
        {items
          .filter((item, idx) => idx < 4)
          .map(singleItem => {
            return <AllItems key={singleItem.id} singleItem={singleItem} />;
          })}
      </div>
    </div>
  );
};

export default PreviewAllItem;
