import React from 'react';
import { Route } from 'react-router-dom';

import HomePage from './pages/homepage/HomePage';
import ShopPage from './pages/shop/ShopPage';
import LoginSignupPage from './pages/login_signup_page/LoginSignupPage';
import NavBar from './components/navbar/NavBar';

import { auth } from './firebase/firebase.utilities';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      currentUser: null,
    };
  }

  unsubscribeFromAuth = null;

  componentDidMount() {
    // An observer method for current user. (The recommended way to get the current user)
    // Firebase feature that allows user persistence. (Open subscriber)
    this.unsubscribeFromAuth = auth.onAuthStateChanged(user => {
      console.log(user);
      if (user) {
        // If user is signed in.
        this.setState({
          currentUser: user,
        });
      } else {
        // No user is signed in.
        this.setState({
          currentUser: null,
        });
      }
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div>
        <NavBar />

        <Route exact path="/" component={HomePage} />
        <Route path="/shop" component={ShopPage} />
        <Route path="/login" component={LoginSignupPage} />
      </div>
    );
  }
}

export default App;
