import { cartActionTypes } from './cart_types';

export const toggleCartHidden = () => ({
  type: cartActionTypes.TOGGLE_CART_HIDDEN,
});

export const addItem = item => ({
  type: cartActionTypes.ADD_ITEM,
  payload: item,
});

export const deleteEntireItem = item => ({
  type: cartActionTypes.DELETE_ENTIRE_ITEM,
  payload: item,
});

export const decrementItem = item => ({
  type: cartActionTypes.DECREMENT_ITEM,
  payload: item,
});

export const clearCart = () => {
  console.log('clear cart dispatched action here =========');
  return {
    type: cartActionTypes.CLEAR_CART,
  };
};

export const clearCartAsync = currentUser => {
  console.log('REUDX THUNK FOR CLEARCART!!!!!!');
  console.log('CURRENT USER>>>', currentUser);

  return async dispatch => {
    try {
      console.log('dispatch???');
      await dispatch(clearCart());
    } catch (error) {
      console.error(error);
    }
  };
};
