import React from 'react';
import { connect } from 'react-redux';

import { removeItem } from '../../redux/cart/cart_action';

import './checkout_item_component.styles.scss';

const CheckoutItemComponent = ({ cartItem, removeItem }) => {
  const { name, imageUrl, price, quantity } = cartItem;

  return (
    <div className="checkout-item">
      <div className="image-container">
        <img src={imageUrl} alt={`item-${name}`} />
      </div>
      <span>{name}</span>
      <span>${price} </span>
      <span>
        <input type="number" id="number" value={quantity} />
      </span>
      <div className="delete-button" onClick={() => removeItem(cartItem)}>
        <i className="fa fa-trash" />
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    removeItem: cartItem => dispatch(removeItem(cartItem)),
  };
};
export default connect(null, mapDispatchToProps)(CheckoutItemComponent);
